for i=1:125
    zsamp(i) = (sum(data(index(i,1:10)))/sqrt(10));
end
mn = mean(zsamp);
vz = var(zsamp);
mn_low = (mn - abs(tinv(alpha/2,114))*sqrt(vz/115))/sqrt(10);
mn_high = (mn + abs(tinv(alpha/2,114))*sqrt(vz/115))/sqrt(10);

