vr_high = 114*(vz/chi2inv((alpha/2),114));

disp([b1,b2,pear,mn_low,mn_high,vr_low,vr_high]);

function res = moment(data,x)
   mn = mean(data); 
   sum = 0;
   for i=1:150
       sum = sum + ((data(i) - mn)^x);
   end
   res = sum/149;
end